CREATE TABLE IF NOT EXISTS exercises (id INT AUTO_INCREMENT PRIMARY KEY, `name` VARCHAR(300) NOT NULL, animation_image_name VARCHAR(1000) NULL, animation_url VARCHAR(1000) NULL, video_url VARCHAR(1000) NULL, images_names TEXT NULL, images_urls TEXT NULL, descriptions_to_images TEXT NULL, images_mistakes_urls TEXT NULL, images_mistakes_names TEXT NULL, descriptions_to_mistakes TEXT NULL, muscles TEXT NULL, muscles_name TEXT NULL, thumbnail_name VARCHAR(1000) NULL, thumbnail_url VARCHAR(1000) NULL);
CREATE TABLE IF NOT EXISTS exercises_to_equipment (id INT AUTO_INCREMENT PRIMARY KEY, exercise_id INT NOT NULL, equipment_id VARCHAR(36) NOT NULL, CONSTRAINT fk_exercises_to_equipment_exercise_id_id FOREIGN KEY (exercise_id) REFERENCES exercises(id) ON DELETE RESTRICT ON UPDATE RESTRICT, CONSTRAINT fk_exercises_to_equipment_equipment_id_id FOREIGN KEY (equipment_id) REFERENCES equipment(id) ON DELETE RESTRICT ON UPDATE RESTRICT);
CREATE TABLE IF NOT EXISTS categories_to_exercises (id INT AUTO_INCREMENT PRIMARY KEY, category_id VARCHAR(36) NOT NULL, exercise_id INT NOT NULL, CONSTRAINT fk_categories_to_exercises_category_id_id FOREIGN KEY (category_id) REFERENCES categories(id) ON DELETE RESTRICT ON UPDATE RESTRICT, CONSTRAINT fk_categories_to_exercises_exercise_id_id FOREIGN KEY (exercise_id) REFERENCES exercises(id) ON DELETE RESTRICT ON UPDATE RESTRICT);